#include <ArduinoJson.h>
#include <SoftwareSerial.h>
String meg;
// Declare the "link" serial port
// Please see SoftwareSerial library for detail
SoftwareSerial linkSerial(16, 17); // RX, TX

void setup() {
  // Initialize "debug" serial port
  // The data rate must be much higher than the "link" serial port
  Serial.begin(115200);
  while (!Serial) continue;

  // Initialize the "link" serial port
  // Use the lowest possible data rate to reduce error ratio
  linkSerial.begin(4800);
}
 
void loop() {
  // Values we want to transmit
  long timestamp = millis();
  int value = analogRead(1);

  // Print the values on the "debug" serial port
  Serial.print("timestamp = ");
  Serial.println(timestamp);
  Serial.print("value = ");
  Serial.println(value);
  Serial.println("---");

  // Create the JSON document
  StaticJsonDocument<5000> doc;
meg = "|timestamp:1656566607715|event:1|action:download|data_size:24026|name:test_image.jpg|type:bw|duration:000|blk:";
 doc["meg"] = meg;

  // Send the JSON document over the "link" serial port
  serializeJson(doc, linkSerial);

  // Wait
  delay(5000);
}
